import React from 'react';
import { List, Typography,Table } from 'antd';
import axios from 'axios'
import baseURL from '../Components/AxiosAPI'

import './List.css'

const columns = [
  {
    title: '品項',
    dataIndex: 'title',
    key: 'title',
    render: text => <a href="javascript:;">{text}</a>,
  },
  {
    title: '庫存',
    dataIndex: 'stock',
    key: 'stock',
    width:'2cm',
  },
  {
    title: '叫貨量',
    dataIndex: 'order',
    key: 'order',
    width:'2cm',
  }
];
const dataSource = [
  {
    key: '1',
    name: '胡彦斌',
    stock: 32,
    order: '西湖区湖底公园1号',
  },
  {
    key: '2',
    name: '胡彦祖',
    stock: 42,
    order: '西湖区湖底公园1号',
  },{
    key: '1',
    name: '胡彦斌',
    stock: 32,
    order: '西湖区湖底公园1号',
  }
];
var income = [
  '營業額'
];
class ListItem extends React.Component{
    constructor(props){
        super(props);
        this.state = {
          data:'',
          expense:'',
          income:'',
          lastUploadDate:'',
          stockTableData:dataSource
        }      
      }
    render(){
      const{data,expense,stockTableData}=this.state
        return(
            <div >
                <h2>{this.state.lastUploadDate}</h2>
                <h3 style={{ marginBottom: 16 }}>庫存</h3>
                <Table columns={columns} dataSource={data} pagination={false} scroll={{ y: 240 }}/>
                <h3 style={{ marginBottom: 16 }}>庫存</h3>

            <List
            bordered
            dataSource={data}
            renderItem={item => (
                <List.Item actions={[<p className='stockvalue' style={{fontSize:'0.8cm'}}>{item.stock}</p>]}>
                {item.title}                
                </List.Item>                
            )}
            />
            <h3 style={{ marginBottom: 16 }}>支出</h3>
            <List
              bordered
              dataSource={expense}
              renderItem={item=>(
                <List.Item actions={[<p className='stockvalue' style={{fontSize:'0.8cm'}}>{item.cost}</p>]}>
                  {item.title}
                </List.Item>
              )}
            />
            <h3 style={{ marginBottom: 16 }}>收入</h3>
            <List
              bordered
              dataSource={income}
              renderItem={item=>(
                <List.Item actions={[<p className='stockvalue' style={{fontSize:'0.8cm'}}>{this.state.income}</p>]}>
                  {'營業額'} 
                </List.Item>
              )}
            />
            </div>

        )
    }
    componentWillMount() {
        console.log('componentWillMount');
          axios.get(baseURL+'/ShopData/getLastStock',
            {
              params: {
                shop : window.localStorage.getItem('shopname'),
                branch : window.localStorage.getItem('branch')
              }
            }
          )
          .then( (response) =>{
          this.setState({data:response.data});
        })
        .catch(function (error) {
          console.log(error);
        }); 

        axios.get(baseURL+'/ShopData/getLastExpense',
        {
          params: {
            shop: window.localStorage.getItem('shopname'),
            branch: window.localStorage.getItem('branch')
          }
        })
        .then( (response) => {
          this.setState({expense:response.data});
        })
        .catch(function (error) {
          console.log(error);
        }); 

        axios.get(baseURL+'/ShopData/getLastIncome',
        {
          params: {
            shop: window.localStorage.getItem('shopname'),
            branch: window.localStorage.getItem('branch')
          }
        })
        .then( (response) => {
          this.setState({income:response.data});
        })
        .catch(function (error) {
          console.log(error);
        }); 
        axios.get(baseURL+'/ShopData/getLastUploadDate',
        {
          params: {
            shop: window.localStorage.getItem('shopname'),
            branch: window.localStorage.getItem('branch')
          }
        })
        .then( (response) => {
          this.setState({lastUploadDate:response.data});
        })
        .catch(function (error) {
          console.log(error);
        }); 
      }
}

export default ListItem
