import React from 'react';
import { List, Typography } from 'antd';
import axios from 'axios'
import baseURL from '../Components/AxiosAPI'

import './List.css'
const data = [
  'Racing car sprays burning fuel into crowd.',
  'Japanese princess to wed commoner.',
  'Australian walks 100km after outback crash.',
  'Man charged over missing wedding girl.',
  'Los Angeles battles huge wildfires.',
];
var income = [
  '營業額'
];
class ListItem extends React.Component{
    constructor(props){
        super(props);
        this.state = {
          data:'',
          expense:'',
          income:'',
          lastUploadDate:''
        }      
      }
    render(){
      const{data,expense}=this.state
        return(
            <div >
                <h2>{this.state.lastUploadDate}</h2>
                <h3 style={{ marginBottom: 16 }}>庫存</h3>
            <List
            bordered
            dataSource={data}
            renderItem={<List.item>{品項}</List.item>,item => (

                <List.Item actions={[<p className='stockvalue' style={{fontSize:'0.8cm'}}>{item.stock}</p>]}>
                {item.title}                
                </List.Item>                
            )}
            />
            <h3 style={{ marginBottom: 16 }}>支出</h3>
            <List
              bordered
              dataSource={expense}
              renderItem={item=>(
                <List.Item actions={[<p className='stockvalue' style={{fontSize:'0.8cm'}}>{item.cost}</p>]}>
                  {item.title}
                </List.Item>
              )}
            />
            <h3 style={{ marginBottom: 16 }}>收入</h3>
            <List
              bordered
              dataSource={income}
              renderItem={item=>(
                <List.Item actions={[<p className='stockvalue' style={{fontSize:'0.8cm'}}>{this.state.income}</p>]}>
                  {'營業額'} 
                </List.Item>
              )}
            />
            </div>

        )
    }
    componentWillMount() {
        console.log('componentWillMount');
          axios.get(baseURL+'/ShopData/getLastStock',
            {
              params: {
                shop : window.localStorage.getItem('shopname'),
                branch : window.localStorage.getItem('branch')
              }
            }
          )
          .then( (response) =>{
          this.setState({data:response.data});
        })
        .catch(function (error) {
          console.log(error);
        }); 

        axios.get(baseURL+'/ShopData/getLastExpense',
        {
          params: {
            shop: window.localStorage.getItem('shopname'),
            branch: window.localStorage.getItem('branch')
          }
        })
        .then( (response) => {
          this.setState({expense:response.data});
        })
        .catch(function (error) {
          console.log(error);
        }); 

        axios.get(baseURL+'/ShopData/getLastIncome',
        {
          params: {
            shop: window.localStorage.getItem('shopname'),
            branch: window.localStorage.getItem('branch')
          }
        })
        .then( (response) => {
          this.setState({income:response.data});
        })
        .catch(function (error) {
          console.log(error);
        }); 
        axios.get(baseURL+'/ShopData/getLastUploadDate',
        {
          params: {
            shop: window.localStorage.getItem('shopname'),
            branch: window.localStorage.getItem('branch')
          }
        })
        .then( (response) => {
          this.setState({lastUploadDate:response.data});
        })
        .catch(function (error) {
          console.log(error);
        }); 
      }
}

export default ListItem
