import React from 'react';
import { Row, Col } from 'antd';
import './AreaChart.css'
import {
    XAxis, YAxis, CartesianGrid, Tooltip,
    AreaChart, Area,
  } from 'recharts';
import axios from 'axios'
import baseURL from '../Components/AxiosAPI'
import { DatePicker } from 'antd';
import moment from 'moment';
const dateFormat = 'YYYY-MM-DD';
const { RangePicker } = DatePicker;

  const defaultdata = [
    {
      name: 'Page A', uv: 4000,
    },
    {
      name: 'Page B', uv: 3000,
    },
    {
      name: 'Page C', uv: 2000,
    },
    {
      name: 'Page D', uv: 2780,
    },
    {
      name: 'Page E', uv: 1890,
    },
    {
      name: 'Page F', uv: 2390,
    },
    {
      name: 'Page G', uv: 3490,
    },
    {
        name: 'Page H', uv: 870,
    },
    {
        name: 'Page I', uv: 1490,
    },
    {
        name: 'Page J', uv: 9490,
    },
    {
      name: "2019-06-04",
      uv: "2000"
    },
    {
      name: "2019-06-04",
      uv: "2500"
    }

  ];

class AreaChartItem extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      data:defaultdata,
      startDate:'',
      endDate:''
    }      
    this.saveRef = ref => {this.refDom = ref};
  }
  render(){
    return(
      <Row>
        <Col span={24}>
          <div ref={this.saveRef}>
            <RangePicker
              defaultValue={[moment(moment().add('day',-7).format('YYYY-MM-DD'), dateFormat), moment(moment().format('YYYY-MM-DD'), dateFormat)]}
              format={dateFormat}
              onChange={this.DatePickerFunction.bind(this)}
            />
              <AreaChart className = "AreaChart"
                width={1500}
                height={700}
                data={this.state.data}
                syncId="anyId"
                margin={{
                  top: 10, right: 30, left: 0, bottom: 0,
                  }}
              >
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="日期" />
              <YAxis />
              <Tooltip />
              <Area type="monotone" dataKey="營業額" stroke="#82ca9d" fill="#82ca9d" />
            </AreaChart>
            <Button onClick={this.onClick} />
          </div>
        </Col>
      </Row>      
    )
  }
  DatePickerFunction(dates, dateStrings) {
    axios.get(baseURL+'/ShopData/getIncomeData',
      {
        params: {
          shopname : window.localStorage.getItem('shopname'),
          branch : window.localStorage.getItem('branch'),
          start:dateStrings[0],
          end:dateStrings[1]
        }
      }
    )
    .then( (response) =>{
      this.setState({data:response.data})
      console.log(this.state.data)
    })
    .catch(function (error) {
      console.log(error);
    }); 
  }
  componentWillMount() {
    console.log('componentWillMount');
    axios.get(baseURL+'/ShopData/getIncome',
      {
        params: {
          shopname : window.localStorage.getItem('shopname'),
          branch : window.localStorage.getItem('branch'),
          start:moment().add('day',-7).format('YYYY-MM-DD'),
          end:moment().format('YYYY-MM-DD')
        }
      }
    )
    .then( (response) =>{
      this.setState({data:response.data})

      console.log(this.state.data)
    })
    .catch(function (error) {
      console.log(error);
    });

  }
}

export default AreaChartItem
