import React from 'react';
import { DatePicker } from 'antd';
import moment from 'moment';
import { Menu, Dropdown, Icon } from 'antd';

import { BrowserRouter as Router, Route, Link } from "react-router-dom";

import './App.css'
import { Layout } from 'antd';
import './FrontEnd/Container/Main.css'
import './FrontEnd/Components/AreaChart.js'
import CSILogo from './FrontEnd/png/Logo.png' 
import AreaChartItem from './FrontEnd/Components/AreaChart.js';
import PieChartItem from './FrontEnd/Components/PieChart.js'
import LoginPage from './FrontEnd/Container/Login';

const {
  Header, Footer, Sider, Content,
} = Layout;

const routes = [
  {
    path: "/",
    exact: true,
    main: () => <h2>Home</h2>
  },
  {
    path: "/bubblegum",
    main: () => <PieChartItem />
  },
  {
    path: "/shoelaces",
    main: () => <AreaChartItem />
  }
];
const { RangePicker } = DatePicker;

const dateFormat = 'YYYY/MM/DD';

const menu = (
  <Menu>
    <Menu.Item key="0" selectable>
      康樂總店
    </Menu.Item>
    <Menu.Item key="1">
      大埔店
    </Menu.Item>
    <Menu.Divider />
    <Menu.Item key="2">
      麻豆店
    </Menu.Item>

  </Menu>
);

class MainPage extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      Shop: '康樂總店'}
  }
  ShopChangeHandle(e){
    console.log(this.props);
  }
  render(){
    return(
      <div className="MainPage">
        <Layout>
          <Header className="Header">
            <RangePicker
              defaultValue={[moment('2018/01/01', dateFormat), moment('2018/01/01', dateFormat)]}
              format={dateFormat}
            />
            <Dropdown onClick={this.ShopChangeHandle.bind(this)} className="Dropdown" overlay={menu} trigger={['hover']}>
              <a className="ant-dropdown-link" href="#">
                {this.state.Shop} <Icon type="down" />
              </a>
            </Dropdown>
          </Header>

          <Layout>
            <Router>
            <Sider className="Sider">
              <img className="CSILOGO" src={CSILogo} />

              <div>
                <ul>
                  <ul style={{ listStyleType: "none", padding: 0 }}>
                    <li>
                      <Link to="/">營收</Link>
                    </li>
                    <li>
                      <Link to="/bubblegum">Test2</Link>
                    </li>
                    <li>
                      <Link to="/shoelaces">Test3</Link>
                    </li>
                  </ul>
                </ul>
              </div>

            </Sider>
            <Content className="Content">
                  <div style={{ display: "flex" }}>
                    <div style={{ flex: 1, padding: "10px" }}>
                      {routes.map((route, index) => (
                        // Render more <Route>s with the same paths as
                        // above, but different components this time.
                        <Route
                          key={index}
                          path={route.path}
                          exact={route.exact}
                          component={route.main}
                        />
                      ))}
                    </div>
                  </div>
            </Content>
            </Router>
            <Footer className="Footer">Footer</Footer>
          </Layout>
        </Layout>

      </div>
    )
  }
}

function App() {
  return (
    <MainPage />
  );
}



