import React from 'react';
import { Menu, Dropdown, Icon, Popover,Button, Avatar, Layout } from 'antd';
import '../../App.css'

class InfoIcon extends React.Component{
    LogoutFunction(){
        window.sessionStorage.setItem('isAuth','false');
      }
    constructor(props){
        super(props);
        this.state = {
          }
    }
    render(){
        return(
            <Popover content={
                <div>
                  {
                    //User
                    <div>
                      <p>姓名：{this.props.Name}</p>
                      <p>位置：{this.props.Branch}</p>
                      <p>權限：{this.props.permission}</p>
                    </div>
                  }
                  <Button className='logout' onClick={this.LogoutFunction.bind(this)}>登出<Icon type="logout" /></Button>
                </div>
              } trigger="click">
                <Avatar className="Avatar" shape={this.props.shape} icon="user" size={this.props.size} />    
              </Popover>
        )
    }

}
export default InfoIcon;
