import React from 'react';
import { DatePicker } from 'antd';
import moment from 'moment';
import { Menu, Dropdown, Icon } from 'antd';

import { BrowserRouter as Router, Route, Link } from "react-router-dom";

import './App.css'
import { Layout } from 'antd';
import './FrontEnd/Container/Main.css'
import './FrontEnd/Components/AreaChart.js'
import CSILogo from './FrontEnd/png/Logo.png' 
import AreaChartItem from './FrontEnd/Components/AreaChart.js';

const {
  Header, Footer, Sider, Content,
} = Layout;

const routes = [
  {
    path: "/",
    exact: true,
    sidebar: () => <div>home!</div>,
    main: () => <h2>Home</h2>
  },
  {
    path: "/bubblegum",
    sidebar: () => <div>bubblegum!</div>,
    main: () => <h2>Bubblegum</h2>
  },
  {
    path: "/shoelaces",
    sidebar: () => <div>shoelaces!</div>,
    main: () => <AreaChartItem />
  }
];
const { RangePicker } = DatePicker;

const dateFormat = 'YYYY/MM/DD';

const menu = (
  <Menu>
    <Menu.Item key="0" selectable>
      康樂總店
    </Menu.Item>
    <Menu.Item key="1">
      大埔店
    </Menu.Item>
    <Menu.Divider />
    <Menu.Item key="2">
      麻豆店
    </Menu.Item>

  </Menu>
);

class MainPage extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      Shop: '康樂總店'}
  }
  ShopChangeHandle(e){
    console.log(this.props);
  }
  render(){
    return(
      <div className="MainPage">
        <Layout>
          <Sider className="Sider">
            <img className="CSILOGO" src={CSILogo} />
            <div>
              <ul>
                <li>營收</li>
                <li>Test2</li>
                <li>Test3</li>
                <li>Test4</li>
                <li>Test5</li>
                <li>Test6</li>
                <Router>
                <ul style={{ listStyleType: "none", padding: 0 }}>
                        <li>
                          <Link to="/">Home</Link>
                        </li>
                        <li>
                          <Link to="/bubblegum">Bubblegum</Link>
                        </li>
                        <li>
                          <Link to="/shoelaces">Shoelaces</Link>
                        </li>
                      </ul>
                </Router>

              </ul>
            </div>

          </Sider>
          <Layout>
            <Header className="Header">
              <RangePicker
                defaultValue={[moment('2018/01/01', dateFormat), moment('2018/01/01', dateFormat)]}
                format={dateFormat}
              />
              <Dropdown onClick={this.ShopChangeHandle.bind(this)} className="Dropdown" overlay={menu} trigger={['hover']}>
                <a className="ant-dropdown-link" href="#">
                  {this.state.Shop} <Icon type="down" />
                </a>
              </Dropdown>
            </Header>
            <Content className="Content">
            <Router>
                  <div style={{ display: "flex" }}>
                    <div
                      style={{
                        padding: "10px",
                        width: "40%",
                        background: "#f0f0f0"
                      }}
                    >


                      {routes.map((route, index) => (
                        <Route
                          key={index}
                          path={route.path}
                          exact={route.exact}
                          component={route.sidebar}
                        />
                      ))}
                    </div>

                    <div style={{ flex: 1, padding: "10px" }}>
                      {routes.map((route, index) => (
                        // Render more <Route>s with the same paths as
                        // above, but different components this time.
                        <Route
                          key={index}
                          path={route.path}
                          exact={route.exact}
                          component={route.main}
                        />
                      ))}
                    </div>
                  </div>
                </Router>  
            </Content>
            <Footer className="Footer">Footer</Footer>
          </Layout>
        </Layout>

      </div>
    )
  }
}

function App() {
  return (
    <MainPage />
  );
}



