import React from 'react';
import Button from 'antd/lib/button';
import './App.css'

class MainPage extends React.Component{
  render(){
    return(
      <div className="App">
        <header className="App-header">

              Main
          <main>
          <Button type="primary">Login</Button>

          </main>
        </header>
      </div>
    )
  }
}

function App() {
  return (
    <MainPage />
  );
}



