import React from 'react';
import { DatePicker } from 'antd';
import moment from 'moment';
import { Menu, Dropdown, Icon } from 'antd';

import { BrowserRouter as Router, Route, Link } from "react-router-dom";

import './App.css'
import { Layout } from 'antd';
import './FrontEnd/Container/Main.css'
import './FrontEnd/Components/AreaChart.js'
import CSILogo from './FrontEnd/png/Logo.png' 
import AreaChartItem from './FrontEnd/Components/AreaChart.js';
import PieChartItem from './FrontEnd/Components/PieChart.js'
import ListItem from './FrontEnd/Components/List.js';

const {
  Header, Footer, Sider, Content,
} = Layout;

const routes = [
  {
    path: "/",
    exact: true,
    main: () => <ListItem />
  },
  {
    path: "/bubblegum",
    main: () => <PieChartItem />
  },
  {
    path: "/shoelaces",
    main: () => <AreaChartItem />
  }
];
const { RangePicker } = DatePicker;

const dateFormat = 'YYYY/MM/DD';

const menu = (
  <Menu>
    <Menu.Item key="0" selectable>
      康樂總店
    </Menu.Item>
    <Menu.Item key="1">
      大埔店
    </Menu.Item>
    <Menu.Divider />
    <Menu.Item key="2">
      麻豆店
    </Menu.Item>

  </Menu>
);

class MainPage extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      Shop: '康樂總店'}
  }
  ShopChangeHandle(e){
    console.log(this.props);
  }
  render(){
    return(
      <div className="MainPage">
        <Layout>
      <Sider>Sider</Sider>
      <Layout>
        <Header>Header</Header>
        <Content>Content</Content>
        <Footer>Footer</Footer>
      </Layout>
    </Layout>

      </div>
    )
  }
}

function App() {
  return (
    <MainPage />
  );
}



