import React from 'react';
import { DatePicker } from 'antd';
import moment from 'moment';
import { Menu, Dropdown, Icon } from 'antd';
import {
  XAxis, YAxis, CartesianGrid, Tooltip,
  AreaChart, Area,
} from 'recharts';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

import './App.css'
import { Layout } from 'antd';
import './FrontEnd/Container/Main.css'

import CSILogo from './FrontEnd/png/Logo.png' 

const {
  Header, Footer, Sider, Content,
} = Layout;

const data = [
  {
    name: 'Page A', uv: 4000, pv: 2400, amt: 2400,
  },
  {
    name: 'Page B', uv: 3000, pv: 1398, amt: 2210,
  },
  {
    name: 'Page C', uv: 2000, pv: 9800, amt: 2290,
  },
  {
    name: 'Page D', uv: 2780, pv: 3908, amt: 2000,
  },
  {
    name: 'Page E', uv: 1890, pv: 4800, amt: 2181,
  },
  {
    name: 'Page F', uv: 2390, pv: 3800, amt: 2500,
  },
  {
    name: 'Page G', uv: 3490, pv: 4300, amt: 2100,
  },
];

const { RangePicker } = DatePicker;

const dateFormat = 'YYYY/MM/DD';

const menu = (
  <Menu>
    <Menu.Item key="0" selectable>
      康樂總店
    </Menu.Item>
    <Menu.Item key="1">
      大埔店
    </Menu.Item>
    <Menu.Divider />
    <Menu.Item key="2">
      麻豆店
    </Menu.Item>

  </Menu>
);

class MainPage extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      Shop: '康樂總店'}
  }
  ShopChangeHandle(e){
    console.log(this.props);
  }
  render(){
    return(
      <div className="MainPage">
        <Layout>
          <Sider className="Sider">
            <img className="CSILOGO" src={CSILogo} />
            <div>
              <ul>
                <li>營收</li>
                <li>Test2</li>
                <li>Test3</li>
                <li>Test4</li>
                <li>Test5</li>
                <li>Test6</li>
              </ul>
            </div>

          </Sider>
          <Layout>
            <Header className="Header">
              <RangePicker
                defaultValue={[moment('2018/01/01', dateFormat), moment('2018/01/01', dateFormat)]}
                format={dateFormat}
              />
              <Dropdown onClick={this.ShopChangeHandle.bind(this)} className="Dropdown" overlay={menu} trigger={['hover']}>
                <a className="ant-dropdown-link" href="#">
                  {this.state.Shop} <Icon type="down" />
                </a>
              </Dropdown>
            </Header>
            <Content className="Content">
              <AreaChart className = "AreaChart"
                width={1500}
                height={700}
                data={data}
                syncId="anyId"
                margin={{
                  top: 10, right: 30, left: 0, bottom: 0,
                }}
              >
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
                <Area type="monotone" dataKey="uv" stroke="#82ca9d" fill="#82ca9d" />
              </AreaChart>
            </Content>
            <Footer className="Footer">Footer</Footer>
          </Layout>
        </Layout>

      </div>
    )
  }
}

function App() {
  return (
    <MainPage />
  );
}



