import React from 'react';
import './User.css'
import { Row, Col, Layout, Button, Input } from 'antd';
import { List, Icon, Popover, Avatar, InputNumber } from 'antd';
import axios from 'axios'
import NumericInput from'../Components/InputNumber'

const defaultData = [
    {
      title: 'Ant Design Title 1',
      key: 1,
      value: 0
    },
    {
      title: 'Ant Design Title 2',
      key: 2,
      value: 0,
    }
  ];
  export default class DemoK extends React.Component {
    state = {
      initLoading: true,
      loading: false,
      list: [],
      value: 0,
      data: defaultData,
    };

    onChange = (item ,e) => {
      const { data} = this.state;
      //console.log('===', e.target.value);
        data.forEach(data => {
        if (data.key === item.key) {
          data.value = e.target.value;
        }
      });
      this.setState({ data });
    }

    render() {
      const { value, data } = this.state;
      return (
        <React.Fragment>
          {
            data.map(item =>
              <List.Item key={item.key}>
                <List.Item.Meta
                  avatar={<Avatar src="" />}
                  title={<a href="https://ant.design">{item.title}</a>}
                />
                <div>
                  <InputNumber key={item.key} placeholder="Basic usage" value={item.value} onChange={(value) => this.onChange(item, value)} />

                </div>
              </List.Item>
            )
          }
        </React.Fragment>
      );
    }
  }
