import React from 'react';
import { Drawer,Button } from 'antd';
import axios from 'axios'
import baseURL from '../Components/AxiosAPI'




class Warning extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            warningData:[],
        }      

      }
    render() {
        const {warningData} = this.state
        return(
            <span>
                <Button className='WarningBtn' type='danger' ghost onClick={this.showDrawer}>警告</Button>
                <Drawer
                  title="Basic Drawer"
                  placement="right"
                  closable={false}
                  onClose={this.onClose}
                  visible={this.state.visible}
                >
                  <p>{warningData}</p>
                </Drawer>
            </span>


        )
    }
    showDrawer = () => {
        this.setState({
          visible: true,
        });
      };

      onClose = () => {
        this.setState({
          visible: false,
        });
      };
    componentWillMount(){
        console.log('warning page')
        axios.get(baseURL+'/ShopData/getWarning',
            {
              params: {
                shop : window.localStorage.getItem('shopname'),
                branch : window.localStorage.getItem('branch'),
                page: 'Main'
              }
            }
          )
          .then( (response) =>{
            this.setState({
                warningData:response.data,
            })
        })
        .catch( (error) => {
          console.log(error);
        }); 
    }
}

export default Warning
