import React from 'react';
import './Achieving.css'
import { Table, Button, DatePicker } from 'antd';
import axios from 'axios'
import baseURL from './AxiosAPI'
import moment from 'moment';
import {
    BarChart, Bar, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ReferenceLine,
  } from 'recharts';
  const data = [
    {
      name: 'Page A', uv: 4000, pv: 1200, amt: 2400,
    },
    {
      name: 'Page B', uv: 3000, pv: 1398, amt: 2210,
    }
  ];

class Achieving extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      data:'',
      startDate:moment().add('day',-7).format('YYYY-MM-DD'),
      endDate:moment().format('YYYY-MM-DD'),
      chartWidth:this.props.Width,
      chartHeight:500,

    }      
    this.saveRef = ref => {this.refDom = ref};
    this.updateDimensions = this.updateDimensions.bind(this)
  }
  render(){
    const{chartWidth}=this.state
    const {clientWidth, clientHeight} = this.refDom;
    return(
      <div ref={this.saveRef}>
        <p> Achieving</p>
        <BarChart
          width={chartWidth}
          height={700}
          data={data}
          margin={{
            top: 20, right: 30, left: 20, bottom: 5,
          }}
          >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="name" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Bar dataKey="uv" fill="#82ca9d" />
          <Bar dataKey="pv" stackId="a" fill="#ff0000" />
          <Bar dataKey="amt" stackId="a" fill="#8884d8" />
        </BarChart>
      </div>
    )
  }

  componentWillMount(){
    window.addEventListener("resize", this.updateDimensions);
  }
  componentWillUnmount(){
    window.removeEventListener("resize", this.updateDimensions);
  }
  componentWillUpdate(){

  }
  updateDimensions = (e) => { 
    console.log("AreaChart updateDimensions");
    const {clientWidth, clientHeight} = this.refDom;
    console.log('====================================');
    console.log(clientWidth, clientHeight, this.refDom);
    console.log('====================================');
    this.setState({
      chartWidth:clientWidth
    });
  }
}

export default Achieving
