import React from 'react';
import { Breadcrumb,Icon } from 'antd';
import { BrowserRouter as Router, Route, Link, withRouter,Switch } from "react-router-dom";
import ProductList from '../Components/ProductList.js'

import './Product.css'

import pic1 from '../pic/1.png' 
import pic2 from '../pic/2.png' 
import pic3 from '../pic/3.png' 
import pic4 from '../pic/4.png' 
import pic5 from '../pic/5.png' 
import pic6 from '../pic/6.png' 

const routes = [
  {
    path: '/',
    breadcrumbName: 'home',
    main: () => <ProductList />

  },
  {
    path: 'product1',
    breadcrumbName: 'product1',
  },
  {
    path: 'product2',
    breadcrumbName: 'product2',
  },
  {
    path: 'product3',
    breadcrumbName: 'product3',
  },
  {
    path: 'product4',
    breadcrumbName: 'product4',
  },
  {
    path: 'product5',
    breadcrumbName: 'product5',
  },
  {
    path: 'product6',
    breadcrumbName: 'product6',
  },
];
const breadcrumbNameMap = {
  '/apps': 'Application List',
  '/apps/1': 'Application1',
  '/apps/2': 'Application2',
  '/apps/1/detail': 'Detail',
  '/apps/2/detail': 'Detail',
};
const Home = withRouter(props => {
  const { location } = props;
  const pathSnippets = location.pathname.split('/').filter(i => i);
  const extraBreadcrumbItems = pathSnippets.map((_, index) => {
    const url = `/${pathSnippets.slice(0, index + 1).join('/')}`;
    return (
      <Breadcrumb.Item key={url}>
        <Link to={url}>{breadcrumbNameMap[url]}</Link>
      </Breadcrumb.Item>
    );
  });
  const breadcrumbItems = [
    <Breadcrumb.Item key="home">
      <Link to="/">Home</Link>
    </Breadcrumb.Item>,
  ].concat(extraBreadcrumbItems);
  return (
    <div className="demo">
      <div className="demo-nav">
        <Link to="/">Home</Link>
        <Link to="/apps">Application List</Link>
      </div>
      <Switch>
        <Route path="/apps" component={Apps} />
        <Route render={() => <span>Home Page</span>} />
      </Switch>
      <Alert style={{ margin: '16px 0' }} message="Click the navigation above to switch:" />
      <Breadcrumb>{breadcrumbItems}</Breadcrumb>
    </div>
  );
});


class Product extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      chartWidth:0,
      chartHeight:250,
    }      
    this.saveRef = ref => {this.refDom = ref};
    this.updateDimensions = this.updateDimensions.bind(this)
  }

  render() {
  return (
      <Router>
        <br/>
        <Home />
        <Breadcrumb separator=">">
          <Breadcrumb.Item href="">
            <Icon type="usappstoreer" />
            <span style={{fontSize:"18px",color:'#C8A065'}}>產品</span>
          </Breadcrumb.Item>
        </Breadcrumb>

        <div className='Product' ref={this.saveRef} style={{ flex: 1, padding: "10px" }}>
          {routes.map((route, index) => (
           // Render more <Route>s with the same paths as
            // above, but different components this time.
            <Route
              key={index}
              path={route.path}
              exact={route.exact}
              component={route.main}
            />
          ))}

        </div>
      </Router>
    )    
  }

  componentWillMount(){
    console.log('componentWillMount');
  }
  componentWillUnmount() {
    console.log('componentWillUnmount');
    window.removeEventListener("resize", this.updateDimensions);
  }
  componentWillUpdate(){
    console.log('componentWillUpdate')
  }
  updateDimensions = (e) => { 
    console.log("StockList updateDimensions");
    const {clientWidth, clientHeight} = this.refDom;
    console.log('====================================');
    console.log(clientWidth, clientHeight, this.refDom);
    console.log('====================================');
    this.setState({
      chartWidth:clientWidth
    });
  }
}

export default Product;
